function panelList(){const e=document.getElementById("panel-list");e.addEventListener("click",t=>{let n=t.target;if(n.classList.contains("parent")){t.preventDefault();let l=n.closest("li").querySelector(".dropdown"),a=l.querySelector(".prev");l.classList.add("active"),a.addEventListener("click",t=>{a.closest(".dropdown").classList.remove("active"),e.classList.remove("hide")})}})}function panelCatalogClick(){const e=document.querySelector(".js-panel-catalog");e.onclick=function(){document.getElementById("panel-catalog").classList.toggle("show"),e.classList.toggle("active"),document.body.classList.toggle("lock")}}function panelHeight(){let e=document.documentElement.clientHeight,t=document.getElementById("panel-catalog"),n=document.getElementById("panel-smart").clientHeight;t.style.height=e-n+"px"}panelList(),panelCatalogClick(),panelHeight(),window.addEventListener("resize",panelHeight),window.addEventListener("load",panelHeight);